from .paginate import paginate_cursor
from typing import Any, Dict, Optional

# Create a CRUD surface for a REST resource at `object_path`.

def make_crud_resource(object_path: str, send):
    def list_(properties: Optional[list[str]] = None, limit: Optional[int] = None, after: Optional[str] = None):
        query: Dict[str, Any] = {}
        if properties:
            query['properties'] = ','.join(properties)
        if limit is not None:
            query['limit'] = limit
        if after is not None:
            query['after'] = after
        return send({ 'method': 'GET', 'path': object_path, 'query': query })

    def get(id: str, properties: Optional[list[str]] = None):
        query: Dict[str, Any] = {}
        if properties:
            query['properties'] = ','.join(properties)
        return send({ 'method': 'GET', 'path': f"{object_path}/{id}", 'query': query })

    def stream_all(properties: Optional[list[str]] = None, page_size: Optional[int] = None):
        query: Dict[str, Any] = {}
        if properties:
            query['properties'] = ','.join(properties)
        for items in paginate_cursor(send=send, path=object_path, query=query, page_size=page_size):
            for item in items:
                yield item

    def get_all(properties: Optional[list[str]] = None, page_size: Optional[int] = None, max_items: Optional[int] = None):
        results: list[Any] = []
        for item in stream_all(properties=properties, page_size=page_size):
            results.append(item)
            if max_items is not None and len(results) >= max_items:
                break
        return results

    return { 'list': list_, 'get': get, 'stream_all': stream_all, 'get_all': get_all }

from ..lib.make_resource import make_crud_resource

__all__ = ['create_resource']


def create_resource(send):
    return make_crud_resource('/{resource}', send)

