children.template:
{
  "name": "{packageName}",
  "version": "0.1.0",
  "private": true,
  "type": "module",
  "main": "dist/index.js",
  "types": "dist/index.d.ts",
  "scripts": {
    "build": "tsup src/index.ts --dts --format esm,cjs",
    "test": "vitest run"
  },
  "engines": {"node": ">=20"},
  "dependencies": {},
  "devDependencies": {
    "tsup": "^8.0.0",
    "typescript": "^5.4.0",
    "vitest": "^1.6.0"
  }
}

{
  "compilerOptions": {
    "target": "ES2022",
    "module": "ESNext",
    "moduleResolution": "Bundler",
    "outDir": "dist",
    "declaration": true,
    "declarationMap": true,
    "strict": true,
    "skipLibCheck": true
  },
  "include": ["src"]
}

{
  "$schema": "https://schemas.connector-factory.dev/schema-index.schema.json",
  "version": "0.1.0",
  "datasets": [
    {
      "name": "events",
      "stage": "raw",
      "kind": "endpoints",
      "path": "raw/endpoints/events.schema.json",
      "doc": "raw/endpoints/events.md"
    },
    {
      "name": "events",
      "stage": "extracted",
      "kind": "endpoints",
      "path": "extracted/endpoints/events.schema.json",
      "doc": "extracted/endpoints/events.md"
    }
  ],
  "_examples": {
    "_comment": "Below are examples of how to use nested folders. Copy and modify these patterns:",
    "endpoint_example": {
      "name": "users.create",
      "stage": "raw",
      "kind": "endpoints",
      "path": "raw/endpoints/endpoints/users/create.schema.json",
      "doc": "raw/endpoints/endpoints/users/create.md",
      "metadata": {
        "category": "endpoint",
        "apiMethod": "POST"
      }
    },
    "nested_organization_example": {
      "name": "reports.analytics",
      "stage": "raw",
      "kind": "endpoints",
      "path": "raw/endpoints/endpoints/reports/analytics.schema.json",
      "doc": "raw/endpoints/endpoints/reports/analytics.md"
    }
  }
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Raw Event",
  "type": "object",
  "additionalProperties": true
}

{
  "tables": []
}

{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Extracted Event",
  "type": "object",
  "additionalProperties": false,
  "properties": {}
}

{
  "tables": []
}

export class Client {
  constructor(public config: Record<string, unknown>) {}
  ping(): boolean { return true }
}

import type { AuthStrategy } from './base'
export class ApiKeyAuth implements AuthStrategy {
  constructor(private apiKey: string) {}
  apply(headers: Record<string,string>) { return { ...headers, Authorization: `Bearer ${this.apiKey}` } }
}

import { describe, it, expect } from 'vitest'
import { Client } from '../src/client'

describe('client', () => {
  it('ping', () => {
    expect(new Client({}).ping()).toBe(true)
  })
})
