# {pipeline}

> Maintained by {author}

Pipeline metadata and assets for the TypeScript {implementation} implementation.

{
  "name": "{packageName}",
  "version": "0.1.0",
  "private": true,
  "main": "dist/index.js",
  "types": "dist/index.d.ts",
  "scripts": {
    "build": "cd app/{resource}; pnpm run build; cd ../..",
    "test": "vitest run",
    "dev": "moose-cli dev",
    "moose": "moose-cli"
  },
  "engines": {"node": ">=20"},
  "dependencies": {
    "@514labs/moose-lib": "latest",
    "dotenv": "^16.4.5",
    "typia": "^9.6.1"
  },
  "devDependencies": {
    "@514labs/moose-cli": "latest",
    "tsup": "^8.0.0",
    "typescript": "^5.4.0",
    "vitest": "^3.2.4"
  }
}

{
  "compilerOptions": {
    "target": "ES2022",
    "module": "ESNext",
    "moduleResolution": "Bundler",
    "outDir": "dist",
    "declaration": true,
    "declarationMap": true,
    "strict": true,
    "skipLibCheck": true
  },
  "include": ["src"]
}

{
  "$schema": "https://schemas.connector-factory.dev/schema-index.schema.json",
  "version": "0.1.0",
  "datasets": [],
  "_examples": {
    "_comment": "Examples for organizing dataset schemas if your pipeline produces tables/files."
  }
}

{
  "version": "1.0",
  "namespace": "moose",
  "nodes": [
    {
      "id": "conn.source",
      "type": "connector",
      "name": "Source Connector",
      "namespace": "{pipeline}",
      "version": "1",
      "attrs": {
        "mode": "etl",
        "schema_hash": "sha256:<hash>",
        "connector": { "name": "<connector-id>", "version": "<vX>", "author": "<author>", "language": "<language>", "implementation": "<implementation>" },
        "identifier": "<external-identifier>",
        "schema_path": "<path/to/repo-relative/schema.json>"
      }
    }
  ],
  "edges": []
}

{
  "$schema": "https://schemas.connector-factory.dev/schema-index.schema.json",
  "version": "0.1.0",
  "datasets": [
    {
      "name": "source-connector",
      "kind": "pointer",
      "connector": { "name": "<connector-id>", "version": "<vX>", "author": "<author>" }
    },
    {
      "name": "transformed.tables",
      "stage": "extracted",
      "kind": "tables",
      "path": "relational/tables.json"
    },
    {
      "name": "outputs.files",
      "stage": "extracted",
      "kind": "files",
      "path": "files/manifest.json"
    }
  ]
}

{
  "tables": [
    { "name": "events", "columns": [ { "name": "event_id", "type": "string" } ] }
  ]
}

{ 
  "files": [ ] 
}

