export class PipelineRunner {
  constructor(public config: Record<string, unknown>) {}
  ping(): boolean { return true }
}

export type HttpResponseEnvelope<T> = { data: T }

export type SendFn = <T = any>(args: {
  method: 'GET' | 'POST' | 'PUT' | 'PATCH' | 'DELETE';
  path: string;
  query?: Record<string, any>;
  headers?: Record<string, string>;
  body?: unknown;
  operation?: string;
}) => Promise<HttpResponseEnvelope<T>>

export async function* paginateCursor<T = any>(params: {
  send: SendFn;
  path: string;
  query?: Record<string, any>;
  pageSize?: number;
  extractItems?: (res: any) => T[];
  extractNextCursor?: (res: any) => string | undefined;
}) {
  const extractItems = params.extractItems ?? ((res: any) => (res?.results ?? []) as T[])
  const extractNext = params.extractNextCursor ?? ((res: any) => res?.paging?.next?.after as string | undefined)
  let after: string | undefined = params.query?.after as string | undefined
  const limit = params.pageSize ?? (params.query?.limit as number | undefined) ?? 100
  // eslint-disable-next-line no-constant-condition
  while (true) {
    const res = await params.send<any>({ method: 'GET', path: params.path, query: { ...(params.query ?? {}), limit, after }, operation: 'paginate' })
    const items = extractItems(res.data)
    yield items
    const next = extractNext(res.data)
    if (!next) break
    after = next
  }
}

import { paginateCursor, type SendFn } from './paginate'

/**
 * makeCrudResource
 * Creates a CRUD surface for a REST resource at `objectPath`.
 */
export function makeCrudResource<TItem, TListResponse, TSingleResponse>(objectPath: string, send: SendFn) {
  const api = {
    list: (params?: { properties?: string[]; limit?: number; after?: string }) => {
      const query: Record<string, any> = {}
      if (params?.properties?.length) query.properties = params.properties.join(',')
      if (params?.limit) query.limit = params.limit
      if (params?.after) query.after = params.after
      return send<TListResponse>({ method: 'GET', path: objectPath, query })
    },
    get: (params: { id: string; properties?: string[] }) => {
      const query: Record<string, any> = {}
      if (params?.properties?.length) query.properties = params.properties.join(',')
      return send<TSingleResponse>({ method: 'GET', path: `${objectPath}/${params.id}`, query })
    },
    streamAll: async function* (params?: { properties?: string[]; pageSize?: number }) {
      const query: Record<string, any> = {}
      if (params?.properties?.length) query.properties = params.properties.join(',')
      for await (const items of paginateCursor<TItem>({ send, path: objectPath, query, pageSize: params?.pageSize })) {
        for (const item of items) yield item
      }
    },
    getAll: async (params?: { properties?: string[]; pageSize?: number; maxItems?: number }) => {
      const results: TItem[] = []
      for await (const item of api.streamAll({ properties: params?.properties, pageSize: params?.pageSize })) {
        results.push(item)
        if (params?.maxItems && results.length >= params.maxItems) break
      }
      return results
    },
  }
  return api
}

export type RequestOptions = {
  method: 'GET' | 'POST' | 'PUT' | 'PATCH' | 'DELETE'
  path: string
  query?: Record<string, any>
  headers?: Record<string, string>
  body?: unknown
  timeoutMs?: number
  operation?: string
}

export type ResponseMeta = {
  timestamp: number
  durationMs: number
  retryCount: number
  requestId?: string
}

export type ResponseEnvelope<T> = {
  data: T
  status?: number
  headers?: Record<string, string>
  meta?: ResponseMeta
}

export type HookContext = {
  type: 'beforeRequest' | 'afterResponse' | 'onError' | 'onRetry'
  request?: RequestOptions
  response?: ResponseEnvelope<any>
  error?: unknown
  metadata?: Record<string, any>
  attempt?: number
}

export type Hook = (ctx: HookContext) => void | Promise<void>

export type Hooks = {
  beforeRequest?: Hook[]
  afterResponse?: Hook[]
  onError?: Hook[]
  onRetry?: Hook[]
}

export async function runHooks(hooks: Hook[] | undefined, ctx: HookContext): Promise<void> {
  for (const hook of hooks ?? []) {
    // eslint-disable-next-line no-await-in-loop
    await hook(ctx)
  }
}

import type { Hooks, RequestOptions, ResponseEnvelope } from './hooks'
import { runHooks } from './hooks'

export type DoRequest = <T = any>(req: RequestOptions) => Promise<ResponseEnvelope<T>>

export type RetryConfig = {
  maxAttempts?: number
  initialDelayMs?: number
  maxDelayMs?: number
  backoffMultiplier?: number
  retryableStatusCodes?: number[]
  respectRetryAfter?: boolean
}

function sleep(ms: number): Promise<void> {
  return new Promise((resolve) => setTimeout(resolve, ms))
}

function calcDelay(attempt: number, cfg: Required<Pick<RetryConfig, 'initialDelayMs' | 'maxDelayMs' | 'backoffMultiplier'>>): number {
  const exp = cfg.initialDelayMs * Math.pow(cfg.backoffMultiplier, attempt - 1)
  const bounded = Math.min(exp, cfg.maxDelayMs)
  const jitter = bounded * (0.5 + Math.random() * 0.5)
  return Math.floor(jitter)
}

export function createSend({ doRequest, hooks, retry }: { doRequest: DoRequest; hooks?: Hooks; retry?: RetryConfig }) {
  const retryCfg: Required<RetryConfig> = {
    maxAttempts: retry?.maxAttempts ?? 3,
    initialDelayMs: retry?.initialDelayMs ?? 1000,
    maxDelayMs: retry?.maxDelayMs ?? 30000,
    backoffMultiplier: retry?.backoffMultiplier ?? 2,
    retryableStatusCodes: retry?.retryableStatusCodes ?? [408, 425, 429, 500, 502, 503, 504],
    respectRetryAfter: retry?.respectRetryAfter ?? true,
  } as Required<RetryConfig>

  async function send<T = any>(req: RequestOptions): Promise<ResponseEnvelope<T>> {
    const startedAt = Date.now()
    let lastError: unknown
    for (let attempt = 1; attempt <= retryCfg.maxAttempts; attempt++) {
      try {
        await runHooks(hooks?.beforeRequest, { type: 'beforeRequest', request: req, attempt })
        const res = await doRequest<T>(req)
        await runHooks(hooks?.afterResponse, { type: 'afterResponse', request: req, response: res, attempt })
        const status = res.status ?? 200
        if (!retryCfg.retryableStatusCodes.includes(status)) {
          res.meta = { ...(res.meta ?? {}), timestamp: Date.now(), durationMs: Date.now() - startedAt, retryCount: attempt - 1 }
          return res
        }
        lastError = new Error(`Retryable status: ${status}`)
      } catch (err) {
        lastError = err
        await runHooks(hooks?.onError, { type: 'onError', request: req, error: err, attempt })
      }
      if (attempt < retryCfg.maxAttempts) {
        await runHooks(hooks?.onRetry, { type: 'onRetry', request: req, error: lastError, attempt })
        const delay = calcDelay(attempt, { initialDelayMs: retryCfg.initialDelayMs, maxDelayMs: retryCfg.maxDelayMs, backoffMultiplier: retryCfg.backoffMultiplier })
        await sleep(delay)
        continue
      }
      break
    }
    throw lastError ?? new Error('Request failed')
  }

  return send
}

